package ArrayDemo;

public class Demo2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int[] abc = new int[5];

		abc[0] = 10;
		abc[1] = 20;
		abc[2] = 30;
		abc[3] = 40;
		abc[4] = 50;

		for(int i=0;i<=4;i++)
		{
			System.out.println(abc[i]);    
			//10 20 30 40 50
		}
	}

}
;