package Demo;

public class LoopCondtionalDemo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		//types loop 
		//		1 do
		//		2 while
		//		3 do -while

		//		1 while
		int i =10;
		while(i<=20) {
			System.out.println(i);
			i++;
		}

		// even number print using while loop

		int j =10;
		while(j<=20 ) 
		{    
			if(j%2==0) {
				System.out.println(j);

			}
			j++;



		}



	}

}
