package Demo;

public class LoopDemo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		for(int i =1; i<=5;i++) {
			System.out.println(i);
		}

		//even number print
		System.out.println("print even number bewteen 10 to 20 ");
		for(int i =10; i<=20;i++) {
			
			if(i%2==0) {
				
				System.out.println(i);
			}
		}


	}

}
